import Pawn from '../pieces/pawn.js';
import Bailiff from '../pieces/bailiff';

export default function initialisGameBoard(){
    const squares = Array(25).fill(null);

    for(let i = 0; i < 5; i++){
        squares[i] = new Pawn(2);
        squares[i+40] = new Pawn(1);
    }
    squares[0] = new Rook(2);
    squares[7] = new Rook(2);
    squares[56] = new Rook(1);
    squares[63] = new Rook(1);

    squares[1] = new Knight(2);
    squares[6] = new Knight(2);
    squares[57] = new Knight(1);
    squares[62] = new Knight(1);

    squares[2] = new Bishop(2);
    squares[5] = new Bishop(2);
    squares[58] = new Bishop(1);
    squares[61] = new Bishop(1);

    squares[3] = new Bailiff(2);
    squares[4] = new King(2);

    squares[59] = new Bailiff(1);
    squares[60] = new King(1);

    return squares;
}
